import React, {ChangeEvent} from 'react';
import { Button, Checkbox } from '@material-ui/core';
import axios from 'axios';
import './App.css';

class App extends React.Component {

    state = {
        textBox: "",
        isChecked: false,
        todos: Array<TodoItems>()
    };

    api: string = 'https://localhost:5001/api/TodoItems/';

    textBox(): string {
        return this.state.textBox;
    }

    render(): JSX.Element {
      return (
          <div className="App">
              <header className="App-header">
                  <ul>
                      { this.state.todos.map((data: TodoItems) => (<li key={data.id}>{ `${data.id}: ${data.name} > ${data.isCompleted}` }</li>)) }
                  </ul>
                  <Button variant="outlined" color="primary" onClick={() => {this.getJSON()}}>Get API</Button>
                  <div>
                      <code> Is Completed : </code>
                      <Checkbox
                          checked={this.state.isChecked}
                          onChange={(event) => this.setCheck(event)}
                          color="primary"
                          inputProps={{ 'aria-label': 'primary checkbox' }}
                      />
                  </div>
                  <Button color="secondary" onClick={() => {this.postJSON(this.state.textBox, )}}>Post API</Button>
              </header>
          </div>
      );
    }

    setCheck(event: ChangeEvent<HTMLInputElement>): void {
        console.log(this.state);
        this.setState({
            textBox: this.textBox(),
            isChecked: event.target.checked,
            todos: this.state.todos
        });
    }

    getJSON(): void {
        axios.get(this.api)
            .then( (response) => {
                // change the list into the state
                const todos: TodoItems[] = response.data.sort((a: TodoItems, b: TodoItems) => {
                    return a.id <= b.id ? -1 : 1;
                });
                this.setState({ todos: todos });
            })
            .catch( (error: Error) => {
                console.error(error);
            });
    }

    postJSON(name: string, isCompleted: boolean = false, dueDate: Date | null = null): void {
        axios.post(this.api, {
            name: name,
            dueDate: dueDate,
            isCompleted: isCompleted
        }).then(_ => {
            this.getJSON();
        }).catch( (error: Error) => {
           console.error(error)
        });
    }
}

interface TodoItems {
    id: number,
    name: string,
    createdAt: Date,
    dueDate: Date | null,
    isCompleted: boolean
}

export default App;
