import React from 'react';
import logo from './logo.svg';
import { Button } from '@material-ui/core';
import axios from 'axios';
import './App.css';

class App extends React.Component {

    state = {
        textBox: "",
        isChecked: false,
        todos: Array<TodoItems>()
    };

    api: string = 'https://localhost:5001/api/TodoItems/';

    textBox(): string {
        return this.state.textBox;
    }

    render(): JSX.Element {
      return (
          <div className="App">
              <header className="App-header">
                  <img src={logo} className="App-logo" alt="logo"/>
                  <ul>
                      { this.state.todos.map((data: TodoItems) => (<li key={data.id}>{ `${data.id}: ${data.name} > ${data.isCompleted}` }</li>)) }
                  </ul>
                  <button onClick={() => {this.getJSON()}}>Get API</button>
                  <button onClick={() => {this.postJSON(this.state.textBox, )}}>Post API</button>
              </header>
          </div>
      );
    }

    getJSON(): void {
        axios.get(this.api)
            .then( (response) => {
                // change the list into the state
                const todos: TodoItems[] = response.data.sort((a: TodoItems, b: TodoItems) => {
                    return a.id <= b.id ? -1 : 1;
                });
                this.setState({ todos: todos });
            })
            .catch( (error: Error) => {
                console.error(error);
            });
    }

    postJSON(name: string, isCompleted: boolean = false, dueDate: Date | null = null): void {
        axios.post(this.api, {
            name: name,
            dueDate: dueDate,
            isCompleted: isCompleted
        }).then(_ => {
            this.getJSON();
        }).catch( (error: Error) => {
           console.error(error)
        });
    }
}

interface TodoItems {
    id: number,
    name: string,
    createdAt: Date,
    dueDate: Date | null,
    isCompleted: boolean
}

export default App;
